function FormCollectionManager(childrenBlock, currentElementsNumber, childrenEntity) {

    //Html block who contain the child pattern (automatically generated by SF)
    this.childrenBlock = childrenBlock;

    //Number of elements of children in current entity
    this.currentElementsNumber = currentElementsNumber;

    //Name for catching adder and remover (parse of data-collection-adder et data-collection-remover where value is this attribute)
    this.childrenEntity = childrenEntity;

    this.init = function() {
        const item = this;
        $("a[data-collection-adder= " + this.childrenEntity + "]").on("click", function() {
            item.addElement();
        });
        this.deleteButtons();
    }

    this.addElement = function() {
        const prototype = this.childrenBlock.data('prototype').replace(/__name__/g, this.currentElementsNumber);
        childrenBlock.append(prototype);
        this.currentElementsNumber ++;
    }

    this.deleteButtons = function () {
        $("a[data-collection-remover= " + this.childrenEntity + "]").each(function() {
            $(this).on('click', function() {
                this.parentNode.parentNode.remove();
            });
        });
    }

}